mapper.select:
		select
		
		from t_menu t
		
			
        		(t.NAME like CONCAT('%', #{search}, '%'))
			
		
		
			order by ${sort} ${order}
		
		limit ${offset},${limit}

mapper.update:
update t_menu
NAME = #{name,jdbcType=VARCHAR}, PARENT_ID = #{parentId,jdbcType=INTEGER}, URL = #{url,jdbcType=VARCHAR}, ICON = #{icon,jdbcType=VARCHAR}, SERIAL_NUM = #{serialNum,jdbcType=INTEGER}, STATUS = #{status,jdbcType=TINYINT}, REMARK = #{remark,jdbcType=VARCHAR}, UPDATE_PERSON = #{updatePerson,jdbcType=INTEGER}, UPDATE_TIME = now(), VERSION = VERSION + 1,
where ID = #{id,jdbcType=INTEGER}
