mapper.select:
select id, name, content, status, create_time, update_time, version
from message
where id = #{id}

    	select t.rownum, t.id, t.name, t.content, t.status, t.create_time, t.update_time, t.version 
    	from (select (@i:=@i+1) as rownum, m.id, m.name, m.content, m.status, m.create_time, m.update_time, m.version 
    		  from message m,(select @i:=0) as row
    	) t 
		 order by ${sort}
		 ${order}
		limit #{offset}, #{limit}
